import React, { useState } from 'react';
import { Button, Modal, Form } from 'react-bootstrap';
import '../ServiceCard.css';
import { Link } from 'react-router-dom';

interface IServiceCardProps {
  id: number;
  name: string;
  minPrice: number;
  subtitle: string;
  description: string;
  extraLine: string;
  backgroundImage: string;
}

const ServiceCard: React.FC<IServiceCardProps> = ({
  id,
  name,
  minPrice,
  subtitle,
  description,
  extraLine,
  backgroundImage,
}) => {
  const [showModal, setShowModal] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
  });

  const handleOpenModal = () => setShowModal(true);
  const handleCloseModal = () => setShowModal(false);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // Here you can send formData to your backend or log it
    console.log('Submitted:', formData);
    handleCloseModal();
  };

  return (
    <div className="service-card">
      {/* Wrap only the clickable part with Link */}
      <Link 
        to={`/services/${id}`} 
        className="service-card-link"
        aria-label={`Перейти к деталям услуги "${name}"`}
      >
        <div
          className="service-card-overlay"
          style={{ backgroundImage: `url(${backgroundImage})` }}
          aria-hidden="true"
        >
          {/* All content inside the overlay */}
          <h3>{name}</h3>
          <p>от {minPrice.toLocaleString()} ₽</p>
          <p>{subtitle}</p>
          <p>{description}</p>
          <p>{extraLine}</p>
        </div>
      </Link>

      {/* Separate button outside the Link */}
      <div className="service-card-buttons">
        <Button variant="outline-light" onClick={handleOpenModal}>
          Мне это интересно
        </Button>
      </div>

      {/* Modal Form */}
      <Modal show={showModal} onHide={handleCloseModal} centered>
        <Modal.Header closeButton>
          <Modal.Title>Заполните форму</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="formName">
              <Form.Label>Ваше имя</Form.Label>
              <Form.Control
                type="text"
                placeholder="Имя"
                name="name"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </Form.Group>
            <Form.Group controlId="formPhone" className="mt-3">
              <Form.Label>Номер телефона</Form.Label>
              <Form.Control
                type="tel"
                placeholder="+7 900 123-45-67"
                name="phone"
                value={formData.phone}
                onChange={handleChange}
                required
              />
            </Form.Group>
            <div className="d-flex justify-content-end mt-4">
              <Button type="submit" variant="primary">Отправить</Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default ServiceCard;
