import React from 'react';
import { Button } from 'react-bootstrap';

interface IServiceCardProps {
  id: number;
  title: string;
  subtitle: string;
  description: string;
  backgroundImage: string;
}

const ServiceCard: React.FC<IServiceCardProps> = ({ title, subtitle, description, backgroundImage }) => {
  return (
    <div
      className="service-card"
      style={{
        position: 'relative',
        borderRadius: '8px',
        overflow: 'hidden',
        height: '300px',
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        color: '#fff',
      }}
    >
      <div
        style={{
          position: 'absolute',
          bottom: 0,
          width: '100%',
          background: 'rgba(0,0,0,0.6)',
          padding: '20px',
        }}
      >
        <h3>{title}</h3>
        <p>{subtitle}</p>
        <p>{description}</p>
        <div style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
          <Button
            variant="primary"
            style={{ backgroundColor: '#FF6B6B', borderColor: '#FF6B6B' }}
          >
            Мне это интересно
          </Button>
          <Button
            variant="primary"
            style={{ backgroundColor: '#007bff', borderColor: '#007bff' }}
          >
            Подробнее
          </Button>
        </div>
      </div>
    </div>
  );
};

export default ServiceCard;
