import React from 'react';
import { Button } from 'react-bootstrap';

interface IServiceCardProps {
  id: number;
  title: string;
  amount: string;
  childName: string;
  activityArea: string;
  footerText: string;
  ctaText: string;
  backgroundImage: string;
}

const ServiceCard: React.FC<IServiceCardProps> = ({
  id,
  title,
  amount,
  childName,
  activityArea,
  footerText,
  ctaText,
  backgroundImage,
}) => {
  return (
    <div
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        height: '300px',
        position: 'relative',
        overflow: 'hidden',
        borderRadius: '8px',
        cursor: 'pointer',
      }}
    >
      <div
        style={{
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'space-between',
          padding: '20px',
          backgroundColor: 'rgba(0, 0, 0, 0.6)',
          color: '#fff',
        }}
      >
        <div>
          <h3>{title}</h3>
          <p><strong>Стоимость:</strong> {amount}</p>
          <p><strong>Фамилия ребенка:</strong> {childName}</p>
          <p><strong>Сфера деятельности:</strong> {activityArea}</p>
        </div>

        <div>
          <p style={{ fontSize: '14px', margin: 0 }}>{footerText}</p>
          <Button
            variant="primary"
            style={{
              backgroundColor: '#007bff',
              borderColor: '#007bff',
              marginTop: '10px',
              color: '#fff',
            }}
          >
            {ctaText}
          </Button>
        </div>
      </div>
    </div>
  );
};

export default ServiceCard;
