import React, { useState, useEffect } from 'react';
import { fetchServices } from '../utils/api';
import ServiceCard from '../components/ServiceCard';
import { Form, Row, Col } from 'react-bootstrap';
import { Service } from '../types';
interface Service {
  id: number;
  title: string;
  description: string;
  price: number;
  date: string;
  imageUrl?: string;
}

const ServicesListPage: React.FC = () => {
  const [services, setServices] = useState<Service[]>([]);
  const [filters, setFilters] = useState({
    name: '',
    minPrice: 0,
    maxPrice: Infinity,
    fromDate: '',
    toDate: ''
  });

  useEffect(() => {
    fetchServices(
      filters.name,
      filters.minPrice,
      filters.maxPrice,
      filters.fromDate,
      filters.toDate
    ).then(setServices);
  }, [filters]);

  return (
    <div>
      <h2>Список услуг</h2>
      <Form>
        <Row>
          <Col md={3}>
            <Form.Group controlId="formName">
              <Form.Label>Фильтр по имени</Form.Label>
              <Form.Control type="text" onChange={(e) => setFilters({ ...filters, name: e.target.value })} />
            </Form.Group>
          </Col>
          <Col md={2}>
            <Form.Group controlId="formMinPrice">
              <Form.Label>Мин. цена</Form.Label>
              <Form.Control type="number" onChange={(e) => setFilters({ ...filters, minPrice: Number(e.target.value) })} />
            </Form.Group>
          </Col>
          <Col md={2}>
            <Form.Group controlId="formMaxPrice">
              <Form.Label>Макс. цена</Form.Label>
              <Form.Control type="number" onChange={(e) => setFilters({ ...filters, maxPrice: Number(e.target.value) })} />
            </Form.Group>
          </Col>
          <Col md={2}>
            <Form.Group controlId="formFromDate">
              <Form.Label>С даты</Form.Label>
              <Form.Control type="date" onChange={(e) => setFilters({ ...filters, fromDate: e.target.value })} />
            </Form.Group>
          </Col>
          <Col md={2}>
            <Form.Group controlId="formToDate">
              <Form.Label>По дату</Form.Label>
              <Form.Control type="date" onChange={(e) => setFilters({ ...filters, toDate: e.target.value })} />
            </Form.Group>
          </Col>
        </Row>
      </Form>

      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '1rem' }}>
        {services.map(service => (
          <ServiceCard key={service.id} {...service} />
        ))}
      </div>
    </div>
  );
};

export default ServicesListPage;
