import React from 'react';
import { Button } from 'react-bootstrap';
import '../ServiceCard.css';
import { Link } from 'react-router-dom';

interface IServiceCardProps {
  id: number;
  name: string;
  minPrice: number;
  subtitle: string;
  description: string;
  extraLine: string;
  backgroundImage: string;
}

const ServiceCard: React.FC<IServiceCardProps> = ({
  id,
  name,
  minPrice,
  subtitle,
  description,
  extraLine,
  backgroundImage,
}) => {
  return (
    <Link 
      to={`/services/${id}`} 
      className="service-card-link"
      aria-label={`Перейти к деталям услуги "${name}"`}
    >
      <div
        className="service-card"
        style={{ backgroundImage: `url(${backgroundImage})` }}
        aria-hidden="true"
      >
        {/* Always visible content */}
        <div className="service-card-title">
          <h3>{name}</h3>
          <p>от {minPrice.toLocaleString()} ₽</p>
        </div>

        {/* Hidden by default, shown on hover */}
        <div className="service-card-overlay">
          <p>{subtitle}</p>
          <p>{description}</p>
          <p>{extraLine}</p>
          <div className="service-card-buttons">
            <Button className="interest-button">Мне это интересно</Button>
          </div>
        </div>
      </div>
    </Link>
  );
};

export default ServiceCard;
