import React from 'react';
import { useParams } from 'react-router-dom';
import { Container } from 'react-bootstrap';

// Define the shape of each service object
interface ServiceDetail {
  id: number;
  title: string;
  subtitle: string;
  description: string;
  backgroundImage: string;
}

// Mock data for the two services (same as in ServicesListPage)
const MOCK_SERVICE_DETAILS: { [key: number]: ServiceDetail } = {
  1: {
    id: 1,
    title: "Заемные средства",
    subtitle: "Формат работы: Бухгалтерский учет",
    description: "Сфера деятельности: Коммерческая",
    backgroundImage: "https://via.placeholder.com/800x500?text=Заемные+средства"
  },
  2: {
    id: 2,
    title: "Основные средства",
    subtitle: "Формат работы: Аудит",
    description: "Сфера деятельности: Промышленность",
    backgroundImage: "https://via.placeholder.com/800x500?text=Основные+средства"
  }
};

const ServiceDetailPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const serviceId = parseInt(id || '1');
  const service = MOCK_SERVICE_DETAILS[serviceId];

  return (
    <Container className="mt-5">
      <h2>Детали услуги</h2>

      {/* Detail Card */}
      <div
        style={{
          backgroundImage: `url(${service?.backgroundImage})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          height: '400px',
          position: 'relative',
          overflow: 'hidden',
          borderRadius: '8px',
          color: '#fff', 
          display: 'flex',
          alignItems: 'flex-end',
          justifyContent: 'center',
          marginBottom: '30px',
        }}
      >
        <div
          style={{
            padding: '30px',
            backgroundColor: 'rgba(0, 0, 0, 0.6)',
            width: '100%',
            textAlign: 'center',
          }}
        >
          <h3>{service?.title}</h3>
          <p>{service?.subtitle}</p>
          <p>{service?.description}</p>
        </div>
      </div>
    </Container>
  );
};

export default ServiceDetailPage;
