import React from 'react';
//import { Card, Button } from 'react-bootstrap';
import { Card, LinkContainer } from 'react-router-bootstrap';
import { Link } from 'react-router-dom';

interface ServiceCardProps {
  id: number;
  title: string;
  description: string;
  imageUrl?: string;
  price: number;
}

const DEFAULT_IMAGE = 'https://via.placeholder.com/300x200?text=Нет+изображения';

const ServiceCard: React.FC<ServiceCardProps> = ({ id, title, description, imageUrl, price }) => {
  return (
    <Card style={{ width: '18rem', marginBottom: '1rem' }}>
      <Card.Img variant="top" src={imageUrl || DEFAULT_IMAGE} />
      <Card.Body>
        <Card.Title>{title}</Card.Title>
        <Card.Text>{description}</Card.Text>
        <Card.Text>Цена: {price} руб.</Card.Text>
		<LinkContainer to={`/service/${id}`}>
        <Button variant="primary" as={Link} to={`/service/${id}`}>Посмотреть детали</Button>
		</LinkContainer>
      </Card.Body>
    </Card>
  );
};

export default ServiceCard;
