import React from 'react';
import { Button } from 'react-bootstrap';

interface IServiceCardProps {
  id: number;
  title: string;
  subtitle: string;
  description: string;
  backgroundImage: string;
}

const ServiceCard: React.FC<IServiceCardProps> = ({ id, title, subtitle, description, backgroundImage }) => {
  return (
    <div
      className="service-card"
      style={{
        position: 'relative',
        overflow: 'hidden',
        borderRadius: '8px',
        cursor: 'pointer',
        height: '300px', // Adjust as needed
      }}
    >
      {/* Background Image */}
      <div
        style={{
          backgroundImage: `url(${backgroundImage})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          height: '100%',
		  position: 'relative',
		color: '#fff',
		borderRadius: '8px',
		overflow: 'hidden',
        }}
      />

      {/* Colored Overlay */}
      <div
        style={{
          position: 'absolute',
          top: '50%',
          left: 0,
          width: '100%',
          height: '50%',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'space-between',
          padding: '20px 20px 40px 20px', // Ensure enough padding for buttons
          backgroundColor: 'rgba(0, 0, 0, 0.6)', // Dark overlay with 60% opacity
          color: '#fff',
        }}
      >
        {/* Title and Subtitle */}
        <div>
          <h3>{title}</h3>
          <p>{subtitle}</p>
        </div>

        {/* Description */}
        <p>{description}</p>

        {/* Buttons */}
        <div
          style={{
            display: 'flex',
            gap: '10px',
          }}
        >
          <Button
            variant="primary"
            style={{ backgroundColor: '#FF6B6B', borderColor: '#FF6B6B', color: '#fff' }}
          >
            Мне это интересно
          </Button>
          <Button
            variant="primary"
            style={{ backgroundColor: '#007bff', borderColor: '#007bff', color: '#fff' }}
          >
            Подробнее
          </Button>
        </div>
      </div>
    </div>
  );
};

export default ServiceCard;
