import React, { useState, MouseEvent, FormEvent } from 'react';
import { Button } from 'react-bootstrap';
import '../ServiceCard.css'; // Assuming you'll add styles for the form here
import { Link } from 'react-router-dom';

interface IServiceCardProps {
  id: number;
  name: string;
  minPrice: number;
  subtitle: string;
  description: string;
  extraLine: string;
  backgroundImage: string;
}

const ServiceCard: React.FC<IServiceCardProps> = ({
  id,
  name: serviceName, // Renamed to avoid conflict with form field name
  minPrice,
  subtitle,
  description,
  extraLine,
  backgroundImage,
}) => {
  const [showInterestForm, setShowInterestForm] = useState(false);
  const [clientName, setClientName] = useState('');
  const [clientPhone, setClientPhone] = useState('');

  const handleInterestButtonClick = (event: MouseEvent<HTMLElement>) => {
    event.preventDefault(); // Prevent the Link from navigating
    event.stopPropagation(); // Stop the event from bubbling up to the Link
    setShowInterestForm(true);
  };

  const handleFormSubmit = (event: FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    event.stopPropagation(); // Stop the event from bubbling up to the Link

    // Basic validation (optional, enhance as needed)
    if (!clientName.trim() || !clientPhone.trim()) {
      alert('Пожалуйста, заполните имя и телефон.');
      return;
    }

    console.log({
      serviceId: id,
      serviceName,
      clientName,
      clientPhone,
    });
    alert(`Спасибо, ${clientName}! Ваша заявка на услугу "${serviceName}" принята. Мы скоро свяжемся с вами по номеру ${clientPhone}.`);

    // Reset form and hide it
    setClientName('');
    setClientPhone('');
    setShowInterestForm(false);
  };

  const handleCancelForm = (event: MouseEvent<HTMLElement>) => {
    event.preventDefault();
    event.stopPropagation();
    setShowInterestForm(false);
    setClientName('');
    setClientPhone('');
  };

  // Prevents Link navigation when clicking on the overlay area while the form is open
  const handleOverlayClick = (event: MouseEvent<HTMLDivElement>) => {
    if (showInterestForm) {
      event.preventDefault();
      event.stopPropagation();
    }
  }

  return (
    <Link
      to={`/services/${id}`}
      className="service-card-link"
      aria-label={`Перейти к деталям услуги "${serviceName}"`}
      // onClick behavior is handled by specific interaction handlers below now
    >
      <div
        className="service-card"
        style={{ backgroundImage: `url(${backgroundImage})` }}
        aria-hidden="true" // Keep this as the visual element is decorative for the Link
      >
        {/* Title at the bottom */}
        <div className="service-card-title">
          <h3>{serviceName}</h3>
          <p>от {minPrice.toLocaleString()} ₽</p>
        </div>

        {/* Overlay: shows details or interest form */}
        <div className="service-card-overlay" onClick={handleOverlayClick}>
          {showInterestForm ? (
            <form className="interest-form" onSubmit={handleFormSubmit} onClick={(e) => e.stopPropagation()}>
              <h4>Заинтересовала услуга?</h4>
              <p>Оставьте ваши контакты, и мы свяжемся с вами.</p>
              <div className="form-group">
                <label htmlFor={`form-name-${id}`}>Имя:</label>
                <input
                  type="text"
                  id={`form-name-${id}`}
                  value={clientName}
                  onChange={(e) => setClientName(e.target.value)}
                  onClick={(e) => e.stopPropagation()} // Prevent clicks on input from closing or navigating
                  required
                  aria-required="true"
                />
              </div>
              <div className="form-group">
                <label htmlFor={`form-phone-${id}`}>Телефон:</label>
                <input
                  type="tel"
                  id={`form-phone-${id}`}
                  value={clientPhone}
                  onChange={(e) => setClientPhone(e.target.value)}
                  onClick={(e) => e.stopPropagation()} // Prevent clicks on input from closing or navigating
                  required
                  aria-required="true"
                />
              </div>
              <div className="interest-form-buttons">
                <Button type="submit" className="submit-interest-button">
                  Отправить
                </Button>
                <Button
                  type="button"
                  variant="secondary" // Using a react-bootstrap variant
                  onClick={handleCancelForm}
                  className="cancel-interest-button"
                >
                  Отмена
                </Button>
              </div>
            </form>
          ) : (
            <>
              <p>{subtitle}</p>
              <p>{description}</p>
              <p>{extraLine}</p>
              <div className="service-card-buttons">
                <Button
                  className="interest-button"
                  onClick={handleInterestButtonClick}
                >
                  Мне это интересно
                </Button>
              </div>
            </>
          )}
        </div>
      </div>
    </Link>
  );
};

export default ServiceCard;
